//import './App.css';
import React from 'react';
import { Header } from "./Header";
import { Main } from "./Main";
import { Footer } from "./Footer";
import { ImagePopup } from "./ImagePopup";
import { PopupWithForm } from "./PopupWithForm";

function App() {
    const [isEditProfilePopupOpen, openEditProfilePopup] = React.useState(false);
    const [isAddPlacePopupOpen, openAddPlacePopup] = React.useState(false);
    const [isEditAvatarPopupOpen, openEditavatarPopup] = React.useState(false);
    //props.isOpen' ? document.querySelector(`popup_type_${props.name}`).classList.add('popup_opened') : document.querySelector(`popup_type_${props.name}`).classList.remove('popup_opened')
    function handleEditAvatarClick() {
        openEditavatarPopup(true)
        //document.querySelector('.popup_updateAvatar') .classList.add('popup_opened')
    }
    function handleEditProfileClick() {
        openEditProfilePopup(true)
    }
    function handleAddPlaceClick() {
        openAddPlacePopup(true)
    }
    function closeAllPopups() {
        openEditavatarPopup(false)
        openEditProfilePopup(false)
        openAddPlacePopup(false)

    }
  return (
  <>
    <div class="page"> 
    <Header />
    <Main onEditProfile={handleEditAvatarClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditProfileClick}/>
    <Footer />
    </div>
<section class="popups">
    <PopupWithForm 
    name={'editAvatar'}
    title={'Обновить аватар'}
    isOpen={isEditAvatarPopupOpen}
    onClose={closeAllPopups}/>
    <PopupWithForm 
    name={'addPlace'}
    title={'Новое место'}
    isOpen={isAddPlacePopupOpen}
    onClose={closeAllPopups}/>
    <PopupWithForm 
    name={'editProfile'}
    title={'Редактировать профиль'}
    isOpen={isEditProfilePopupOpen}
    onClose={closeAllPopups}/>
    <ImagePopup />
</section>
 <template class="template" id="">
    <li class="elements__item">
        <button class="elements__delete-btn transition" type="button" area-label="удалить"></button>
        <img  class="elements__img" src="#" alt="#" />
        <div class="elements__title">
            <h2 class="elements__title-text"></h2>
            <div class="elements__like-container">
                <button class="elements__like-icon" type="button" aria-label="Нравится">
                </button>
                <p class="elements__like-number"></p>
            </div>
        </div>
    </li>
 </template>
 </>
  );
}

export default App;
