//import './App.css';
import { Header } from "./Header";
import { Main } from "./Main";
import { Footer } from "./Footer";
import { ImagePopup } from "./ImagePopup";
import { PopupWithForm } from "./PopupWithForm";

function App() {
    const [isVisible, openPopup] = React.useState(false);

    function handleEditAvatarClick() {
        openPopup(true)
        //document.querySelector('.popup_updateAvatar') .classList.add('popup_opened')
    }
    function handleEditProfileClick() {
        document.querySelector('.popup_editForm') .classList.add('popup_opened')
    }
    function handleAddPlaceClick() {
        document.querySelector('.popup_addCard') .classList.add('popup_opened')
    }
  return (
  <>
    <div class="page"> 
    <Header />
    <Main onEditProfile={handleEditAvatarClick} onAddPlace={handleAddPlaceClick} и onEditAvatar={handleEditProfileClick}/>
    <Footer />
    </div>
<section class="popups">
    <PopupWithForm isOpen={isVisible}/>
    <ImagePopup />
</section>
 <template class="template" id="">
    <li class="elements__item">
        <button class="elements__delete-btn transition" type="button" area-label="удалить"></button>
        <img  class="elements__img" src="#" alt="#" />
        <div class="elements__title">
            <h2 class="elements__title-text"></h2>
            <div class="elements__like-container">
                <button class="elements__like-icon" type="button" aria-label="Нравится">
                </button>
                <p class="elements__like-number"></p>
            </div>
        </div>
    </li>
 </template>
 </>
  );
}

export default App;
