export function PopupWithForm(props) {
console.log(document.querySelector(`popup_type_${props.name}`))
//props.isVisible ? document.querySelector(`popup_type_${props.name}`).classList.add('popup_opened') : document.querySelector(`popup_type_${props.name}`).classList.remove('popup_opened')
return (
    <div className={`popup popup_type_${props.name}`}   >
    <form className="edit-form edit-form_profile" name={props.name} novalidate>
        <button className="popup__close-button transition" type="button" area-label="Закрыть"></button>
        <h2 className="edit-form__header">{props.title}</h2>
        {props.children}
    </form>
</div>
)
}
