//import './App.css';
import React from 'react';
import { Header } from "./Header";
import { Main } from "./Main";
import { Footer } from "./Footer";
import { ImagePopup } from "./ImagePopup";
import { PopupWithForm } from "./PopupWithForm";

function App() {
    const [isEditProfilePopupOpen, openEditProfilePopup] = React.useState(false);
    const [isAddPlacePopupOpen, openAddPlacePopup] = React.useState(false);
    const [isEditAvatarPopupOpen, openEditavatarPopup] = React.useState(false);
    //props.isOpen' ? document.querySelector(`popup_type_${props.name}`).classList.add('popup_opened') : document.querySelector(`popup_type_${props.name}`).classList.remove('popup_opened')
    function handleEditAvatarClick() {
        openEditavatarPopup(true)
        //document.querySelector('.popup_updateAvatar') .classList.add('popup_opened')
    }
    function handleEditProfileClick() {
        openEditProfilePopup(true)
    }
    function handleAddPlaceClick() {
        openAddPlacePopup(true)
    }
    function closeAllPopups() {
        openEditavatarPopup(false)
        openEditProfilePopup(false)
        openAddPlacePopup(false)

    }
  return (
  <>
    <div className="page"> 
    <Header />
    <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}/>
    <Footer />
    </div>
<section className="popups">
    <PopupWithForm 
    name={'editAvatar'}
    title={'Обновить аватар'}
    isOpen={isEditAvatarPopupOpen}
    onClose={closeAllPopups}>
        <input className="edit-form__input edit-form__avatarLink" id="edit-form__avatarLink" placeholder="Ссылка на аватар" type="url" name="link" value="" required />
        <span className="edit-form__error" id="edit-form__avatarLink-error"></span>
        <button className="edit-form__button" type="submit" aria-label="Сохранить">Сохранить</button>
    </PopupWithForm>

    <PopupWithForm 
    name={'addPlace'}
    title={'Новое место'}
    isOpen={isAddPlacePopupOpen}
    onClose={closeAllPopups}/>
    <PopupWithForm 
    name={'editProfile'}
    title={'Редактировать профиль'}
    isOpen={isEditProfilePopupOpen}
    onClose={closeAllPopups}/>
    <ImagePopup />
</section>
 <template className="template" id="">
    <li className="elements__item">
        <button className="elements__delete-btn transition" type="button" area-label="удалить"></button>
        <img  className="elements__img" src="#" alt="#" />
        <div className="elements__title">
            <h2 className="elements__title-text"></h2>
            <div className="elements__like-container">
                <button className="elements__like-icon" type="button" aria-label="Нравится">
                </button>
                <p className="elements__like-number"></p>
            </div>
        </div>
    </li>
 </template>
 </>
  );
}

export default App;
