import React from 'react';
import {api} from '../utils/api'
import avatar from '../images/Старик_Букашкин.jpg'

export function Main (props) {
    const [userName, setUserName] = React.useState('Б.У.Кашкин');
    const [userDescription, setUserDescription] = React.useState('Уральский поэт');
    const [userAvatar, setUserAvatar] = React.useState(avatar);
    const [cards, renderCards] = React.useState('');

    React.useEffect(() => {
        api.getUserData()
        .then((res)=> {
            setUserName(res.name)
            setUserDescription(res.about)
            setUserAvatar(res.avatar)
        })
        .catch(err => console.log(err));
      });

      React.useEffect(() => {
        api.getInitialCards()
        .then((res)=> {
            //renderCards(res)
            cards.map((card) => {
                return(
                    <li className="elements__item">
                        <button className="elements__delete-btn transition" type="button" area-label="удалить"></button>
                        <div  className="elements__img" style={{ backgroundImage: `url(${card.link})` }} alt={card.name} />
                        <div className="elements__title">
                            <h2 className="elements__title-text">{card.name}</h2>
                            <div className="elements__like-container">
                                <button className="elements__like-icon" type="button" aria-label="Нравится">
                                </button>
                                <p className="elements__like-number"></p>
                            </div>
                        </div>
                    </li>
                )
            })
        })
        .catch(err => console.log(err));
      });

    return(
    <main className="content">
            <section className="profile">
                <div className="profile__info">
                    <button className="profile__avatar-button" type="button" aria-label="Сменить_аватар" 
                    onClick={props.onEditAvatar}/>
                    <div className="profile__avatar" style={{ backgroundImage: `url(${userAvatar})` }}  alt="фото профиля" />
                    <div className="profile__grid">
                      <div className="profile__name-wrapper">
                        <h1 className="profile__name">{userName}</h1>
                        <button className="profile__edit-button transition" type="button" aria-label="Редактировать_профиль"
                        onClick={props.onEditProfile}/>
                      </div>
                        <p className="profile__about">{userDescription}</p>
                    </div>
                </div>
                <button className="profile__add-button transition" type="button" aria-label="Добавить_изображение"
                onClick={props.onAddPlace}/>
            </section>
            <section className="elements">
                 <ul className="elements__list"/>
            </section>
        </main>
    )
}
